package com.evan.euler;

import java.math.BigInteger;

public class ProblemTwentyFive {

	public static void main(String args[]) {
		double answer = 0;
		
		answer = ProblemTwentyFive();
		
		System.out.println(answer);
	}
	
	public static double ProblemTwentyFive(){
		double count = 0;
		BigInteger solution = new BigInteger("");
		BigInteger tertiary = BigInteger.valueOf(1);
		BigInteger first = BigInteger.valueOf(1);
		BigInteger second = BigInteger.valueOf(1);
				
		while (tertiary.compareTo(solution)<1) {
			
			tertiary = first;
			first = second;
			second = tertiary.add(second);
			count++;
		}
		
	//	System.out.print(second);
		
		return count;
	}
	
	
}
